import React, { useState, useEffect } from 'react';
//Formulario para crear los viajes
function ViajesForm() {
    const [origen, setOrigen] = useState('');
    const [destino, setDestino] = useState('');
    const [dia, setDia] = useState('');
    const [suggestions, setSuggestions] = useState([]);

    const handleSubmit = async (event) => {
        event.preventDefault();

        // Enviar datos al backend
        const response = await fetch('http://localhost:3000/v1/crearViajes', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ origen, destino, dia }),
        });

        // Procesar la respuesta del backend
        const data = await response.json();
        console.log(data); // Aquí puedes manejar la respuesta del backend, que contendrá los vuelos disponibles
    };
    useEffect(() => {
        const fetchSuggestions = async (input) => {
          try {
            const response = await fetch(`http://localhost:3000/v1/suggestions?q=${input}`);
            if (response.ok) {
              const data = await response.json();
              setSuggestions(data);
            } else {
              console.error('Error al obtener las sugerencias');
            }
          } catch (error) {
            console.error('Error al enviar la solicitud:', error);
          }
        };
    
        fetchSuggestions(origin); // Obtener sugerencias para el origen cuando cambia
      }, [origin]);
    
      const handleOriginChange = (event) => {
        setOrigin(event.target.value);
      };
    
      const handleDestinationChange = (event) => {
        setDestination(event.target.value);
      };
    return (
        <div>
            <h2>Buscar Vuelos</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Origen:
                    <input type="text" value={origen} onChange={(e) => setOrigen(e.target.value)} />
                </label>
                <label>
                    Destino:
                    <input type="text" value={destino} onChange={(e) => setDestino(e.target.value)} />
                </label>
                <label>
                    Día:
                    <input type="date" value={dia} onChange={(e) => setDia(e.target.value)} />
                </label>
                <button type="submit">Buscar</button>
            </form>
        </div>    
            ); 
}
export default ViajesForm;
