import React, { useState, useEffect } from 'react';

function BookingForm() {
    const [origen, setOrigen] = useState('');
    const [destino, setDestino] = useState('');
    const [dia, setDia] = useState('');
    const [suggestionsOrigen, setSuggestionsOrigen] = useState([]);
    const [suggestionsDestino, setSuggestionsDestino] = useState([]);

    const handleSubmit = async (event) => {
        event.preventDefault();

        // Enviar datos al backend
        const response = await fetch('http://localhost:3000/v1/crearViajes', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ origen, destino, dia }),
        });

        // Procesar la respuesta del backend
        const data = await response.json();
        console.log(data); // Aquí puedes manejar la respuesta del backend, que contendrá los vuelos disponibles
    };

    useEffect(() => {
        const fetchSuggestions = async (input) => {
            try {
                const response = await fetch(`http://localhost:3000/v1/getSuggestions`);
                if (response.ok) {
                    const data = await response.json();
                    return data;
                } else {
                    console.error('Error al obtener las sugerencias');
                    return [];
                }
            } catch (error) {
                console.error('Error al enviar la solicitud:', error);
                return [];
            }
        };

        // Obtener sugerencias para el origen cuando cambia
        if (origen) {
            fetchSuggestions(origen).then((data) => setSuggestionsOrigen(data));
        } else {
            setSuggestionsOrigen([]);
        }

        // Obtener sugerencias para el destino cuando cambia
        if (destino) {
            fetchSuggestions(destino).then((data) => setSuggestionsDestino(data));
        } else {
            setSuggestionsDestino([]);
        }
    }, [origen, destino]);

    return (
        <div>
            <h2>Buscar Vuelos</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Origen:
                    <input type="text" value={origen} onChange={(e) => setOrigen(e.target.value)} />
                    {/* Renderizar sugerencias para el origen */}
                    <ul>
                        {suggestionsOrigen.map((suggestion, index) => (
                            <li key={index}>{suggestion.name}</li>
                        ))}
                    </ul>
                </label>
                <label>
                    Destino:
                    <input type="text" value={destino} onChange={(e) => setDestino(e.target.value)} />
                    {/* Renderizar sugerencias para el destino */}
                    <ul>
                        {suggestionsDestino.map((suggestion, index) => (
                            <li key={index}>{suggestion.name}</li>
                        ))}
                    </ul>
                </label>
                <label>
                    Día:
                    <input type="date" value={dia} onChange={(e) => setDia(e.target.value)} />
                </label>
                <button type="submit">Buscar</button>
            </form>
        </div>
    );
}

export default BookingForm;
