import React, { useState } from 'react';

const BookingForm = ({ onSelect, placeholder }) => {
    const [origen, setOrigen] = useState('');
    const [destino, setDestino] = useState('');
    const [suggestionsOrigen, setSuggestionsOrigen] = useState([]);
    const [suggestionsDestino, setSuggestionsDestino] = useState([]);
    const [showSuggestionsOrigen, setShowSuggestionsOrigen] = useState(false);
    const [showSuggestionsDestino, setShowSuggestionsDestino] = useState(false);
    // let url_base = `http://localhost:4000/v1/api-reservas/`;
    let url_base = `https://reservas-back-flame.vercel.app/v1/api-reservas/`;

    const handleOriginChange = async (event) => {
        const inputValue = event.target.value;
        setOrigen(inputValue);
        fetchSuggestions(inputValue, setSuggestionsOrigen, setShowSuggestionsOrigen);
    };

    const handleDestinationChange = async (event) => {
        const inputValue = event.target.value;
        setDestino(inputValue);
        fetchSuggestions(inputValue, setSuggestionsDestino, setShowSuggestionsDestino);
    };

    const fetchSuggestions = async (input, setSuggestions, setShowSuggestions) => {
        if (input.length > 0) {
            fetch(`${url_base}search-paises`, {
                method: 'POST',
                body: JSON.stringify({ busqueda: input }),
                headers: {
                    'Content-Type': 'application/json'
                }
            })
            .then(res => res.json())
            .then(response => {
                setSuggestions(response);
                setShowSuggestions(true);
            })
            .catch(error => {
                console.error('Error al obtener las sugerencias:', error);
            });
        } else {
            setSuggestions([]);
            setShowSuggestions(false);
        }
    };

    const handleSuggestionClick = (suggestion, setField, setShowSuggestions) => {
        setField(suggestion);
        setShowSuggestions(false);
    };
      
    return (
        <div className="bookingFormContainer">
            <h2>Buscar Vuelos</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Origen:
                    <input type="text" className="inputField" value={origen} onClick={() => setShowSuggestionsOrigen(true)} onBlur={() => setTimeout(() => setShowSuggestionsOrigen(false), 100)} onChange={handleOriginChange} />
                    <div className="suggestionList">
                        {showSuggestionsOrigen && (
                            <ul>
                                {suggestionsOrigen.map((suggestion, index) => (
                                    <li key={index} className="suggestionItem" onClick={() => handleSuggestionClick(suggestion, setOrigen, setShowSuggestionsOrigen)}>
                                        {suggestion}
                                    </li>
                                ))}
                            </ul>
                        )}
                    </div>
                </label>
                <label>
                    Destino:
                    <input type="text" className="inputField" value={destino} onClick={() => setShowSuggestionsDestino(true)} onBlur={() => setTimeout(() => setShowSuggestionsDestino(false), 100)} onChange={handleDestinationChange} />
                    <div className="suggestionList">
                        {showSuggestionsDestino && (
                            <ul>
                                {suggestionsDestino.map((suggestion, index) => (
                                    <li key={index} className="suggestionItem" onClick={() => handleSuggestionClick(suggestion, setDestino, setShowSuggestionsDestino)}>
                                        {suggestion}
                                    </li>
                                ))}
                            </ul>
                        )}
                    </div>
                </label>
                <button type="submit" className="button">Buscar</button>
            </form>
        </div>
    );
};

export default BookingForm;
